    {
        public string strDocumento;

        public string FormatoHTML(InputData data)
        {

            strDocumento = @"<!doctype html>																													";
            strDocumento += @"<html>                                                                                                                            ";
            strDocumento += @"<head>                                                                                                                            ";
            strDocumento += @"    <style>                                                                                                                       ";
